import "core-js/modules/es.array.push.js";
import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.for-each.js";
import "core-js/modules/es.iterator.map.js";
export default {
  name: "Threat",
  data() {
    return {
      threatRelationChart: null,
      evidenceChart: null,
      selectedEvent: null,
      threatEvents: [],
      rawRecentAbnormalEvents: [],
      departmentAbnormalCountsData: [],
      employeeAbnormalActivitiesData: {},
      isLoading: false
    };
  },
  async mounted() {
    await this.loadThreatData();
    this.initThreatRelationChart();
    window.addEventListener("resize", this.handleResize);
  },
  beforeDestroy() {
    window.removeEventListener("resize", this.handleResize);
    if (this.threatRelationChart) {
      this.threatRelationChart.dispose();
    }
    if (this.evidenceChart) {
      this.evidenceChart.dispose();
    }
  },
  methods: {
    async loadThreatData() {
      this.isLoading = true;
      this.$message.info("正在加载威胁情报数据...");
      try {
        const [recentRes, deptRes, empRes] = await Promise.all([fetch("/recent_abnormal_events.json"), fetch("/department_abnormal_counts.json"), fetch("/employee_abnormal_activities.json")]);
        if (!recentRes.ok) throw new Error("加载最近异常事件失败");
        this.rawRecentAbnormalEvents = await recentRes.json();
        if (!deptRes.ok) throw new Error("加载部门异常统计失败");
        this.departmentAbnormalCountsData = await deptRes.json();
        if (!empRes.ok) throw new Error("加载员工异常活动失败");
        this.employeeAbnormalActivitiesData = await empRes.json();
        this.processThreatEvents();
        this.updateThreatRelationChart();
        this.$message.success("威胁情报数据加载完成！");
      } catch (error) {
        console.error("加载威胁数据失败:", error);
        this.$message.error(`加载威胁数据失败: ${error.message}`);
        this.processThreatEvents();
        this.updateThreatRelationChart();
      } finally {
        this.isLoading = false;
      }
    },
    processThreatEvents() {
      if (this.rawRecentAbnormalEvents && this.rawRecentAbnormalEvents.length > 0) {
        this.threatEvents = this.rawRecentAbnormalEvents.map((event, index) => ({
          id: index + 1,
          type: event.type || "未知类型",
          date: event.timestamp ? event.timestamp.split(" ")[0] : "未知日期",
          time: event.timestamp ? event.timestamp.split(" ")[1] : "未知时间",
          user: event.employeeId || "未知用户",
          description: event.description || "无描述",
          level: event.level || "未知",
          detailDescription: `${event.description}. Raw event: ${JSON.stringify(event.rawEvent, null, 2)}`,
          rawEventData: event
        }));
      } else {
        this.threatEvents = [];
      }
    },
    initThreatRelationChart() {
      if (this.threatRelationChart) {
        this.threatRelationChart.dispose();
      }
      const chartDom = document.getElementById("threatRelationChart");
      if (chartDom) {
        this.threatRelationChart = this.$echarts.init(chartDom);
        this.updateThreatRelationChart();
      } else {
        console.error("Threat relation chart DOM not found.");
      }
    },
    updateThreatRelationChart() {
      if (!this.threatRelationChart) return;
      let nodes = [];
      let links = [];
      const categories = [{
        name: "员工",
        itemStyle: {
          color: "#409EFF"
        }
      },
      // Category 0
      {
        name: "异常类型",
        itemStyle: {
          color: "#F56C6C"
        }
      } // Category 1
      // Możemy dodać więcej kategorii w przyszłości
      ];
      if (!this.employeeAbnormalActivitiesData || Object.keys(this.employeeAbnormalActivitiesData).length === 0) {
        this.threatRelationChart.setOption({
          title: {
            text: "暂无员工异常活动数据以生成关联图",
            left: "center",
            top: "center"
          },
          series: [] // Ensure series is empty if no data
        });
        return;
      }
      const employeeNodes = new Map(); // Store employee nodes to avoid duplicates: employeeId -> nodeIndex
      const activityTypeNodes = new Map(); // Store activity type nodes: typeName -> nodeIndex
      let nodeCounter = 0;
      for (const empId in this.employeeAbnormalActivitiesData) {
        if (Object.hasOwnProperty.call(this.employeeAbnormalActivitiesData, empId)) {
          // Add employee node if not exists
          if (!employeeNodes.has(empId)) {
            nodes.push({
              id: `emp_${empId}`,
              name: `员工 ${empId}`,
              category: 0,
              // Employee category
              symbolSize: 35,
              value: this.employeeAbnormalActivitiesData[empId].length,
              // Size by number of activities
              draggable: true
            });
            employeeNodes.set(empId, nodeCounter++);
          }
          const activities = this.employeeAbnormalActivitiesData[empId];
          activities.forEach(activity => {
            const activityType = activity.type || "未知类型";
            // Add activity type node if not exists
            if (!activityTypeNodes.has(activityType)) {
              nodes.push({
                id: `type_${activityType.replace(/\s+/g, "_")}`,
                name: activityType,
                category: 1,
                // Activity Type category
                symbolSize: 25,
                value: 1,
                // Initial value, can be incremented if multiple employees have this type
                draggable: true
              });
              activityTypeNodes.set(activityType, nodeCounter++);
            } else {
              // Increment value for existing activity type node if needed for sizing
              const existingNodeIndex = activityTypeNodes.get(activityType);
              if (nodes[existingNodeIndex]) nodes[existingNodeIndex].value++;
            }

            // Add link from employee to activity type
            links.push({
              source: `emp_${empId}`,
              target: `type_${activityType.replace(/\s+/g, "_")}`,
              value: 1 // Can be used for line thickness if needed
            });
          });
        }
      }

      // Fallback if no nodes were created (e.g. empty activities for all employees)
      if (nodes.length === 0) {
        this.threatRelationChart.setOption({
          title: {
            text: "未从数据中提取出有效的关联节点",
            left: "center",
            top: "center"
          },
          series: []
        });
        return;
      }
      const option = {
        title: {
          text: "威胁事件关联分析",
          subtext: "员工与异常活动类型关联",
          left: "center"
        },
        tooltip: {
          formatter: function (params) {
            if (params.dataType === "node") {
              return `${params.data.name}<br/>关联活动数: ${params.data.value || "N/A"}`;
            }
            return params.name;
          }
        },
        legend: [{
          data: categories.map(function (a) {
            return a.name;
          }),
          bottom: 10
        }],
        animationDurationUpdate: 1500,
        animationEasingUpdate: "quinticInOut",
        series: [{
          type: "graph",
          layout: "force",
          data: nodes,
          links: links,
          categories: categories,
          roam: true,
          label: {
            show: true,
            position: "right",
            formatter: "{b}" // Show name
          },
          // symbolSize: 50, // Symbol size can be set per node or globally
          edgeSymbol: ["none", "arrow"],
          // No symbol for source, arrow for target
          edgeSymbolSize: [4, 10],
          force: {
            repulsion: 150,
            // Adjusted repulsion
            edgeLength: [80, 120],
            // Varied edge length
            gravity: 0.1
          },
          lineStyle: {
            opacity: 0.9,
            width: 2,
            curveness: 0
          }
        }]
      };
      this.threatRelationChart.setOption(option, true); // true to clear previous options
    },
    handleEventSelect(event) {
      this.selectedEvent = event;
      this.$nextTick(() => {
        this.initEvidenceChart();
      });
    },
    initEvidenceChart() {
      if (this.evidenceChart) {
        this.evidenceChart.dispose();
      }
      if (!this.selectedEvent) return;
      const chartDom = document.getElementById("evidenceChart");
      if (chartDom) {
        this.evidenceChart = this.$echarts.init(chartDom);
        this.updateEvidenceChart();
      } else {
        console.error("Evidence chart DOM not found.");
      }
    },
    updateEvidenceChart() {
      if (!this.evidenceChart || !this.selectedEvent || !this.selectedEvent.rawEventData) {
        // Clear chart or show placeholder if no event or no raw data
        if (this.evidenceChart) {
          this.evidenceChart.setOption({
            title: {
              text: this.selectedEvent ? "选择的事件缺少原始数据" : "未选择事件",
              left: "center",
              top: "center"
            },
            series: [] // Clear series
          });
        }
        return;
      }
      const eventType = this.selectedEvent.type;
      const rawEvent = this.selectedEvent.rawEventData.rawEvent; // Assuming rawEventData contains the original rawEvent object
      let option = {};
      if (eventType === "邮件敏感词" && rawEvent && rawEvent.subject) {
        // 为邮件敏感词显示主题和关键词
        const keywordsFound = [];
        // SENSITIVE_EMAIL_KEYWORDS is not directly available here,
        // but we can infer from the description or rawEvent if it was stored.
        // For now, just display the subject.
        // A more advanced version could highlight keywords in the subject.
        option = {
          title: {
            text: `证据: ${eventType}`,
            subtext: `邮件主题: ${rawEvent.subject.substring(0, 100)}${rawEvent.subject.length > 100 ? "..." : ""}`,
            left: "center",
            textStyle: {
              fontSize: 14
            },
            subtextStyle: {
              fontSize: 12,
              color: "#555"
            }
          },
          // We could use a graphic element to display text if no chart is suitable
          graphic: {
            type: "text",
            left: "center",
            top: "middle",
            style: {
              text: `相关邮件主题与描述中提到的敏感词有关。\n详细信息请查看事件描述。`,
              textAlign: "center",
              font: "14px Microsoft YaHei"
            }
          },
          series: []
        };
      } else if (eventType === "非工作时间登录" && this.selectedEvent.rawEventData) {
        // 为非工作时间登录显示登录时间戳
        option = {
          title: {
            text: `证据: ${eventType}`,
            subtext: `登录时间: ${this.selectedEvent.rawEventData.timestamp}`,
            left: "center",
            textStyle: {
              fontSize: 14
            },
            subtextStyle: {
              fontSize: 12,
              color: "#555"
            }
          },
          graphic: {
            type: "text",
            left: "center",
            top: "middle",
            style: {
              text: `该登录事件发生在非工作时段。\n具体时间点: ${this.selectedEvent.rawEventData.timestamp}`,
              textAlign: "center",
              font: "14px Microsoft YaHei"
            }
          },
          series: []
        };
      } else {
        // 其他事件类型或缺少具体信息
        option = {
          title: {
            text: `事件证据 (类型: ${eventType})`,
            subtext: "此事件类型的详细证据展示待实现",
            left: "center",
            top: "center",
            textStyle: {
              fontSize: 14
            },
            subtextStyle: {
              fontSize: 12,
              color: "#888"
            }
          },
          series: []
        };
      }
      this.evidenceChart.setOption(option, true);
    },
    getEventTypeTag(type) {
      const typeMap = {
        数据外发: "danger",
        账户探测: "warning",
        异常行为: "warning",
        数据库异常: "danger",
        可疑通信: "danger"
      };
      return typeMap[type] || "info";
    },
    getRiskLevelTag(level) {
      const levelMap = {
        高: "danger",
        中: "warning",
        低: "info"
      };
      return levelMap[level] || "info";
    },
    handleResize() {
      this.threatRelationChart && this.threatRelationChart.resize();
      this.evidenceChart && this.evidenceChart.resize();
    }
  }
};
